import Vue from "vue";
import Vuex from 'vuex';

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    helloState: 'hello',
  },

  mutations: {
    save (state, payload) {
      state.count += payload.amount
    }
  }
})
