  },

  setup() {
    const imageList = ref([]);
    const combinedList = ref([]);
    const isRealData = ref(false);
    const fakeData = ref([]);
    fakeData.value.push({
      image: 'https://www.missionjuno.swri.edu/Vault/VaultOutput?VaultID=44805&ts=1656511106',
